mapper.update:
UPDATE FREEBOARD 
SET F_BOARD_TITLE = #{fBoardTitle}, F_BOARD_CONTENTS = #{fBoardContents}, F_BULLET = #{fBullet} 
WHERE F_BOARD_NO = #{fBoardNo}

UPDATE FREEBOARD 
SET HITS = HITS + 1 
WHERE F_BOARD_NO = #{fBoardNo}

mapper.delete:
UPDATE FREEBOARD 
SET F_STATUS = 'N' 
WHERE F_BOARD_NO = #{fBoardNo}
